<form class="form-horizontal" novalidate (ngSubmit)="save()" [formGroup]="formGroup">
  <fieldset>
    <div class="form-group"
         [ngClass]="{'has-error': (formGroup.get('nombre')!.touched || formGroup.get('nombre')!.dirty) && !formGroup.get('nombre')!.valid }">
      <label class="col-md-2 control-label" for="nombreId">Nombre</label>
      <div class="col-md-8">
        <input class="form-control" id="nombreId" type="text" required formControlName="nombre" [readonly]="modoVerDetalle" />
        <span class="help-block" *ngIf="(formGroup.get('nombre')!.touched || formGroup.get('nombre')!.dirty) && formGroup.get('nombre')!.errors">
          <span class="text-danger" *ngIf="formGroup.get('nombre')!.errors!.required">
            El campo nombre es requerido
          </span>
        </span>
      </div>
    </div>

    <div class="form-group"
         [ngClass]="{'has-error': (formGroup.get('descripcion')!.touched || formGroup.get('descripcion')!.dirty) && !formGroup.get('descripcion')!.valid }">
      <label class="col-md-2 control-label" for="descripcionId">Descripcion</label>
      <div class="col-md-8">
        <input class="form-control" id="descripcionId" type="text" required formControlName="descripcion" [readonly]="modoVerDetalle" />
        <span class="help-block" *ngIf="(formGroup.get('descripcion')!.touched || formGroup.get('descripcion')!.dirty) && formGroup.get('descripcion')!.errors">
          <span class="text-danger" *ngIf="formGroup.get('descripcion')!.errors!.required">
            El campo descripcion es requerido
          </span>
        </span>
      </div>
    </div>

    <div class="form-group"
         [ngClass]="{'has-error': (formGroup.get('ubicacion')!.touched || formGroup.get('ubicacion')!.dirty) && !formGroup.get('ubicacion')!.valid }">
      <label class="col-md-2 control-label" for="ubicacionId">Ubicación</label>
      <div class="col-md-8">
        <input class="form-control" id="ubicacionId" type="text" required formControlName="ubicacion" [readonly]="modoVerDetalle" />
        <span class="help-block" *ngIf="(formGroup.get('ubicacion')!.touched || formGroup.get('ubicacion')!.dirty) && formGroup.get('ubicacion')!.errors">
          <span class="text-danger" *ngIf="formGroup.get('ubicacion')!.errors!.required">
            El campo ubicación es requerido
          </span>
        </span>
      </div>
    </div>

    <div class="form-group"
         [ngClass]="{'has-error': (formGroup.get('informacionContacto')!.touched || formGroup.get('informacionContacto')!.dirty) && !formGroup.get('informacionContacto')!.valid }">
      <label class="col-md-2 control-label" for="contactoId">Información de Contacto</label>
      <div class="col-md-8">
        <input class="form-control" id="contactoId" type="text" required formControlName="informacionContacto" [readonly]="modoVerDetalle" />
        <span class="help-block" *ngIf="(formGroup.get('informacionContacto')!.touched || formGroup.get('informacionContacto')!.dirty) && formGroup.get('informacionContacto')!.errors">
          <span class="text-danger" *ngIf="formGroup.get('informacionContacto')!.errors!.required">
            El campo información de contacto es requerido
          </span>
        </span>
      </div>
    </div>

    <div class="form-group"
         [ngClass]="{'has-error': (formGroup.get('fechaInicio')!.touched || formGroup.get('fechaInicio')!.dirty) && !formGroup.get('fechaInicio')!.valid }">
      <label class="col-md-2 control-label" for="fechaInicio">Fecha Inicio</label>
      <div class="col-md-8">
        <input class="form-control" id="fechaInicio" type="date" required formControlName="fechaInicio" [readonly]="modoVerDetalle" />
        <span class="help-block" *ngIf="(formGroup.get('fechaInicio')!.touched || formGroup.get('fechaInicio')!.dirty) && formGroup.get('fechaInicio')!.errors">
          <span class="text-danger" *ngIf="formGroup.get('fechaInicio')!.errors!.required">
            El campo Fecha Inicio es requerido
          </span>
        </span>
      </div>
    </div>

    <div class="form-group"
         [ngClass]="{'has-error': (formGroup.get('fechaFin')!.touched || formGroup.get('fechaFin')!.dirty) && !formGroup.get('fechaFin')!.valid }">
      <label class="col-md-2 control-label" for="fechaFin">Fecha Fin</label>
      <div class="col-md-8">
        <input class="form-control" id="fechaFin" type="date" required formControlName="fechaFin" [readonly]="modoVerDetalle" />
        <span class="help-block" *ngIf="(formGroup.get('fechaFin')!.touched || formGroup.get('fechaFin')!.dirty) && formGroup.get('fechaFin')!.errors">
          <span class="text-danger" *ngIf="formGroup.get('fechaFin')!.errors!.required">
            El campo Fecha Fin es requerido
          </span>
        </span>
      </div>
    </div>

    <div class="form-group">
      <label class="col-md-2 control-label" for="estadoId">Estado</label>
      <div class="col-md-8">
        <input class="form-control" id="estadoId" type="text" formControlName="estado" [readonly]="modoVerDetalle" />
      </div>
    </div>
    <br>

    <div class="form-group">
      <div class="col-md-4 col-md-offset-2">
        <span *ngIf="!modoVerDetalle">
          <button class="btn btn-primary" type="submit" [disabled]="!formGroup.valid">
            Guardar
          </button>
        </span>
        <button class="btn btn-primary" type="button" [routerLink]='["/eventos"]'>
          {{modoVerDetalle ? 'Atrás' : 'Cancelar'}}
        </button>
      </div>
    </div>
  </fieldset>
</form>
